import style from "./test.css";
import jsondata from "./dic";

function doSearch(text) {
    if (window.find && window.getSelection) { 
        document.designMode = "on";
        var sel = window.getSelection();
        sel.collapse(document.body, 0);
        while (window.find(text)) {
            // document.execCommand("insertHTML" , false , "<span className = 'tooltiplink' , datatooltip='new word'>new</span>")
            // document.execCommand("insertHTML" , false , "<span className = {styles.found} >new</span>")
            document.execCommand("insertHTML" , false , `<span style = 'color : red'>${text}</span>`)
            sel.collapseToEnd();
        }
        document.designMode = "off";
    } 
}
doSearch("he");
alert(jsondata["is"]);
