/* global chrome */
import './App.css';
import React from 'react';
import jsondata from "./new.js";

function doSearch(text) {
  if (window.find && window.getSelection) { 
      document.designMode = "on";
      var sel = window.getSelection();
      sel.collapse(document.body, 0);
      while (window.find(text)) {
          // document.execCommand("insertHTML" , false , "<span className = 'tooltiplink' , datatooltip='new word'>new</span>")
          // document.execCommand("insertHTML" , false , "<span className = {styles.found} >new</span>")
          document.execCommand("insertHTML" , false , "<span style = 'color : red'>${'text'}</span>")
          sel.collapseToEnd();
      }
      document.designMode = "off";
  } 
}


function App() {
  doSearch("he");
  __onWindowLoad();
  return (
    <div className="App">
      <header className="App-header">
        <p><strong>slang-translate</strong></p>
      </header>
    </div>
  );
}

function __onWindowLoad() {
  chrome.extension.onMessage.addListener(function(request, sender) {
    if(request.action == "getSource") {
      document.body.innerText = request.source;
    }
  });

  function onWindowLoad() {
    chrome.tabs.executeScript(null, {
      file: "getSource.js"
      }, function() {
      if(chrome.extension.lastError) {
        document.body.innerText = 'Error : \n' + chrome.extension.lastError.message;
      }
    });
  }

  window.onload = onWindowLoad;
}





export default App;
