/* global chrome */
import './App.css';
import React from 'react'
import dic_js from "./dic.js";
import found from "./test.css";

function newfunc2(){

}

function newfunc3(){
  chrome.extension.sendMessage({
    action: "style",
    source: found
});
}


function App() {
  // newfunc2();
  //newfunc3();
  __onWindowLoad();
  return (
    <div className="App">
      <header className="App-header">
        <p><strong>slang-translate</strong></p>
      </header>
    </div>
  );
}

function __onWindowLoad() {
  chrome.extension.onMessage.addListener(function(request, sender) {
    if(request.action == "getSource") {
      document.body.innerText = request.source;
    }
  });

  function onWindowLoad() {
    chrome.extension.sendMessage({
      action: "jsondata",
      source: dic_js
     });
    chrome.tabs.executeScript(null, {
      file: "getSource.js"
      }, function() {
      if(chrome.extension.lastError) {
        document.body.innerText = 'Error : \n' + chrome.extension.lastError.message;
      }
    });
  }

  window.onload = onWindowLoad;
}

export default App;
